import data from './data.json';
import './Header.css';
import Bottom from './Bottom';
// import Bottom from 'components/Bottom';

var images = data.pages.map(value => require(`./backgrounds/${value.blocks[0].background}`));


// export const Header = () => {
class Header extends React.Component{
    constructor(){
        super();
        this.state = {
            index : 0
        }}
    render(){    
    return (

       <div className="header">

        {/* <img src="./abc.png" alt="logo"></img>


         {data.pages.map((data, key) => {
          return (
            <div>
              {data.title }
            </div>
          );
        })} */}

<div style={{
                backgroundImage: `url(${images[this.state.index].default})`
                }} className="main-container">
                <div className="menu">
                    <div className="contactbox">
                        <img id="logo" src="./abc.png" alt="website-logo"/>
                        <div id="contact-button">
                          Contact Us
                        </div>
                    </div>
                    <div onClick={() => this.setState({index: 0})} className="nav-link">
                      Industries
                    </div>
                    <div onClick={() => this.setState({index: 1})} className="nav-link">
                      Services
                    </div>
                    <div onClick={() => this.setState({index: 2})} className="nav-link">
                      About Us
                    </div>
                </div>
                <Bottom index={this.state.index} />
            </div>



    </div>




)
}}
export default Header;
